mapper.select:
select

from shop A
left join goods g on A.id = g.shop_id
left join user u on A.user_id = u.id
where 1=1


	GROUP BY ${groupBy}


	ORDER BY A.${orderBy}


	${ascOrdesc}


	order by A.id desc


	LIMIT ${start},${count}

mapper.update:
update shop
set
finish_time = #{finishTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

UPDATE
deleted_at = NOW(), updated_by = #{updatedBy,jdbcType=VARCHAR}, updated_at = NOW()
WHERE id = #{id,jdbcType=INTEGER}

UPDATE
deleted_at = NULL, updated_by = #{updatedBy,jdbcType=VARCHAR}, updated_at = NOW()
WHERE id = #{id,jdbcType=INTEGER}

UPDATE
${expression1}, ${expression2}, ${expression3}, ${expression4}, ${expression5}, updated_by = #{updatedBy,jdbcType=VARCHAR}, updated_at = NOW()
WHERE id = #{id,jdbcType=INTEGER}
